import React, {useContext} from 'react'
import gql from 'graphql-tag'
import {useQuery} from '@apollo/react-hooks'
import { Card, CardMeta, Grid, Label,Button,Image,Icon } from 'semantic-ui-react';
import moment from 'moment';
import {useParams} from 'react-router-dom'


import LikeButton from '../components/LikeButton';
import {AuthContext} from '../context/auth'
import DeleteButton from '../components/DeleteButton';


function SinglePost() {

    
    const {postId} =useParams();
    const {user}= useContext(AuthContext)
    const {data:{getPost}={}}= useQuery(FETCH_POST_QUERY, {
        variables:{
            postId
        }
    })
    let postMarkup;
    if(!getPost){
        postMarkup= <p>Loading post..</p>
    } else {
        const {id,body,createdAt,username,likes,likeCount,commentCount} =  getPost;
        console.log('this is likesCount')
        console.log(likesCount);
        postMarkup = (
            <Grid>
                <Grid.Row>
                    <Grid.Column width={2}>
                    <Image
                    src='https://react.semantic-ui.com/images/avatar/large/molly.png'
                    size="small"
                    float="right"
                    />
                    </Grid.Column>
                    <Grid.Column width={10}>
                    <Card fluid>
                        <Card.Content>
                            <Card.Header>
                                {username}
                            </Card.Header>
                            <Card.Meta> {moment(createdAt).fromNow()}</Card.Meta>
                            <Card.Description>{body}</Card.Description>
                        </Card.Content>
                        <hr/>
                        <Card.Content>
                        <LikeButton user={user} post={{id, likes,likeCount,likes}}/>
                            <Button 
                             as="div"
                             labelPosition="right"
                             onClick={( )=> console.log('Comment on post')}
                             >
                                 <Button basic color="blue">
                                     <Icon name="comments"/>
                                 </Button>
                                
                                <Label basic color="blue" pointing="left">
                                    {commentCount}
                                </Label> 
                            </Button>
                            {user && user.username ===username && (
                                <DeleteButton postId={id} />
                            )}     
                        </Card.Content>
                    </Card>
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        )
    }
    return postMarkup;
}

const FETCH_POST_QUERY = gql`
    query($postId: ID!) {
        getPost(postId: $postId){
            id body createdAt username likeCount
            likes {
                username
            }
            commentCount
            comments {
                id username createdAt body
            }
        }
    }

`

export default SinglePost
