import React, {useContext} from 'react'
import {Route, Navigate, Routes,useNavigate, BrowserRouter} from 'react-router-dom'

import { AuthContext } from '../context/auth'

function AuthRoute({component: Component,...rest}) {
    const  {user} = useContext(AuthContext);
    return (
        <BrowserRouter>
            <Routes>
                <Route {...rest}
                render={(props)=>
                user ? <Route path='/' element={<Home/>} /> : <Component {...props}/>
                }
                />
            </Routes>
        </BrowserRouter>        
    )
}
