import React from "react";
import {useQuery} from '@apollo/react-hooks';
import gql from 'graphql-tag';
import { Grid, Image } from 'semantic-ui-react';


const FETCH_POSTS_QUERY = gql`{
    getPosts {
        id
        body createdAt
        username
        likeCount
        likes {
            username
        }
        comments {
            id username createdAt body
        }
    }
}
`

export default Home;
