import React, {useContext,useState} from 'react'
import gql from 'graphql-tag'
import {useQuery,useMutation} from '@apollo/react-hooks'
import { Card, Form, Grid, Label,Button,Image,Icon, CommentActions } from 'semantic-ui-react';
import moment from 'moment';
import {useParams} from 'react-router-dom'


import LikeButton from '../components/LikeButton';
import {AuthContext} from '../context/auth'
import DeleteButton from '../components/DeleteButton';


function SinglePost() {

    const[comment,setComment] =useState('');
    const {postId} =useParams();
    const {user}= useContext(AuthContext)
    const {data:{getPost}={}}= useQuery(FETCH_POST_QUERY, {
        variables:{
            postId
        }
    })

    const [submitComment] = useMutation(SUBMIT_COMMENT_MUTATION,{
        update(){
            setComment('')
        },
        variables:{
            postId,
            body: comment
        }
    })

    let postMarkup;
    if(!getPost){
        postMarkup= <p>Loading post..</p>
    } else {
        const {id,body,createdAt,username,likes,likeCount,commentCount,comments} =  getPost;
        console.log('this is likesCount')
        console.log(likeCount);
        postMarkup = (
            <Grid>
                <Grid.Row>
                    <Grid.Column width={2}>
                    <Image
                    src='https://react.semantic-ui.com/images/avatar/large/molly.png'
                    size="small"
                    float="right"
                    />
                    </Grid.Column>
                    <Grid.Column width={10}>
                    <Card fluid>
                        <Card.Content>
                            <Card.Header>
                                {username}
                            </Card.Header>
                            <Card.Meta> {moment(createdAt).fromNow()}</Card.Meta>
                            <Card.Description>{body}</Card.Description>
                        </Card.Content>
                        <hr/>
                        <Card.Content>
                        <LikeButton user={user} post={{id, likes,likeCount,likes}}/>
                            <Button 
                             as="div"
                             labelPosition="right"
                             onClick={( )=> console.log('Comment on post')}
                             >
                                 <Button basic color="blue">
                                     <Icon name="comments"/>
                                 </Button>
                                
                                <Label basic color="blue" pointing="left">
                                    {commentCount}
                                </Label> 
                            </Button>
                            {user && user.username ===username && (
                                <DeleteButton postId={id} />
                            )}     
                        </Card.Content>
                    </Card>
                    {user && (
                        <Card fluid>
                            <Card.Content>
                            <p>Post Comment</p>
                            <Form>
                                <div classname="ui action input fluid">
                                    <input type="text"
                                    placeholder='Comment..'
                                    name='comment'
                                    value={comment}
                                    onChange={event => setComment(event.target.value)}
                                    />
                                    <button type="submit"
                                        className="ui button teal"
                                        disabled={comment.trim()===''}
                                        onClick={submitComment}>
                                            Submit
                                        </button>
                                </div>
                            </Form>
                            </Card.Content>
                        </Card>
                    )}
                    {comments.map(comment => (
                        <Card fluid key={comment.id}>
                            <Card.Content>
                                {user && user.username === comment.username &&(
                                    <DeleteButton postId={id} commentId={comment.id}/>
                                )}
                                <Card.Header>{comment.username}</Card.Header>
                                <Card.Meta>{moment(comment.createdAt).fromNow()}</Card.Meta>
                                <Card.Description>{comment.body}</Card.Description>
                            </Card.Content>
                        </Card>
                    ))}
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        )
    }
    return postMarkup;
}

const FETCH_POST_QUERY = gql`
    query($postId: ID!) {
        getPost(postId: $postId){
            id body createdAt username likeCount
            likes {
                username
            }
            commentCount
            comments {
                id username createdAt body
            }
        }
    }

`

const SUBMIT_COMMENT_MUTATION = gql `
    mutation($postId: String!,$body:String!){
        createComment(postId:$postId,body:$body){
            id
            comments{
                id body createdAt username
            }
            commentCount
        }
    }

`

export default SinglePost
