      <Grid.Column>
        <Image src='/images/wireframe/media-paragraph.png' />
      </Grid.Column>
    </Grid.Row>
    </Grid>
    )
}

const FETCH_POSTS_QUERY = gql`{
    getPosts {
        id
        body createdAt
        username
        likeCount
        likes {
            username
        }
        comments {
            id username createdAt body
        }
    }
}
`

export default Home;
